mapper.select:
select 
from 

  1 = 1

    and plate = #{plate,jdbcType=VARCHAR}


    and complany_name = #{companyName,jdbcType=VARCHAR}


    and functions = #{functions,jdbcType=VARCHAR}


    and phone like concat('%',#{phone,jdbcType=VARCHAR},'%')


    and file_name like concat('%',#{fileName,jdbcType=VARCHAR},'%')


    and leader = #{leader,jdbcType=VARCHAR}


    AND DATE_FORMAT(file_time,'%Y-%m-%d') >= #{receiptStartTime}


    AND DATE_FORMAT(file_time,'%Y-%m-%d') <= #{receiptEndTime}


    AND DATE_FORMAT(handle_time,'%Y-%m-%d') >= #{handleStartTime}


    AND DATE_FORMAT(handle_time,'%Y-%m-%d') <= #{handleEndTime}
